mapper.select:
select gotoImageReal
from tbl_courseInfo
where gotoNumber = #{gotoNumber}

select *
from tbl_courseInfo
where courseNumber = #{courseNumber} and gotoDate = #{gotoDate}

select gotoLocationX
from tbl_courseInfo
where gotoNumber = #{gotoNumber}

select gotoLocationY
from tbl_courseInfo
where gotoNumber = #{gotoNumber}
