mapper.select:
select max(courseNumber)
from tbl_Course
where userNumber = #{userNumber}

select max(courseNumber)
from tbl_Course
where userNumber = #{userNumber}

select courseName
from tbl_Course
where userNumber = #{userNumber} and courseName = #{courseName }

select courseName
from tbl_Course
where courseNumber = #{courseNumber}

mapper.update:
update tbl_course
set courseName = #{courseName}, story = #{story}, isPosted = true
where courseNumber = #{courseNumber}

mapper.select:
select distinct(courseNumber) from tbl_course where isPosted = true and courseNumber IN (select distinct(courseNumber) from tbl_courseInfo where
(gotoAreaCode = #{item.areaCode} and gotoSigunguCode = #{item.sigunguCode})
)

mapper.update:
update tbl_Course
set story = null, isPosted = false
where courseNumber = #{courseNumber}
